// Config secion
// #################
// adapt those to make changes to the simulation character
// PT2 characteristics
let a2 = 100
let a1 = 2
// Interval in seconds when a new rand setPoint is generated
let pointInterval = 30
let pointMin = 30
let pointMax = 50

// This value must correspond to the trigger nodes interval
let sampleInterval = 1
let sampleRate = 1/(sampleInterval)

let limit = sampleRate * pointInterval
let deltaT = 1 / sampleRate;
let deltaTSquare = deltaT * deltaT;
let psi = a2 / (deltaTSquare) + a1 / deltaT + 1.0;

let countdown = context.get('countdown') || limit;
countdown = countdown -1 
context.set('countdown', countdown)
if (countdown <= 0) {
    context.set('countdown', Math.random() * 10)
    let newPoint = Math.floor(Math.random()*(pointMax-pointMin+1)+pointMin);
    context.set('setPoint', newPoint);
}

let setPoint = context.get('setPoint') || 40

let k_2 = context.get('k_2') || 0
let k_1 = context.get('k_1') || 0

let value = setPoint / psi + (2 * a2 * k_1) / (psi * deltaTSquare) - (a2 * k_2) / (psi * deltaTSquare) + (a1 * k_1) / (psi * deltaT);
context.set('k_2', k_1)
context.set('k_1', value)

value = value.toFixed(2)

// make it part of the outgoing msg object
msg.payload = {sensorname: "temperature", timestamp: Math.floor(Date.now() / 1000), value: value};

return msg;
